$(document).ready(function(){$("#gunlist").DataTable({dom:"Bfrtip",buttons:["colvis","excel","print"]});$("#damage").on("blur",function(){var t=$(this).val(),i=t.split("*"),r=i.length,u=1,n;if(r>1){for(n=0;n<r;n++)u*=i[n];$(this).val(u)}else $(this).val(t)});var n=$("#searchResults");$("#gunname").on("keyup",function(){n.empty();var t=$(this).val();$.get("GunNameSearch?ss="+t,function(t){for(var r=$.parseJSON(t),i=0;i<r.length;i++)n.append("<div class='row'> <div class='col-md-1'>"+r[i].Level+"<\/div> <div class='col-md-4'>"+r[i].Name+"<\/div> <div class='col-md-2'>"+r[i].DamageOnTarget+"<\/div> <div class='col-md-2'>"+r[i].AllTypesDamageOnTargetRank+"<\/div> <\/div>")})});$("#damage, #accuracy,  #firerate, #reloadspeed, #magazinesize, #elementalDamagePerSecond, #chance").on("keyup",function(){var n=$("#damage").val(),t=$("#accuracy").val(),i=$("#firerate").val(),r=$("#reloadspeed").val(),u=$("#magazinesize").val(),f=$("#elementalDamagePerSecond").val(),e=$("#chance").val();$.get("GunCalcs?damage="+n+"&accuracy="+t+"&firerate="+i+"&reloadspeed="+r+"&magazinesize="+u+"&elementalDamagePerSecond="+f+"&chance="+e,function(n){var t=$.parseJSON(n);$("#damageTimesFireRate").val(t.DamageTimesFireRate);$("#damageTimesFireRateTimesMagazineSize").val(t.DamageTimesFireRateTimesMagazineSize);$("#damageTimesFireRateTimesMagazineSizePerReloadSpeed").val(t.DamageTimesFireRateTimesMagazineSizePerReloadSpeed);$("#damageOnTarget").val(t.DamageOnTarget);$("#elementalDamagePerSecondTimesChance").val(t.ElementalDamagePerSecondTimesChance);$("#elementalDamageOnTargetTimesDamagePerSecondTimesChance").val(t.ElementalDamageOnTargetTimesDamagePerSecondTimesChance)})})});